DROP TABLE IF EXISTS account_groups;
create table account_groups(ssn varchar(40), first_name varchar(40),  last_name varchar(40), group_name varchar(50));
DROP PROCEDURE IF EXISTS move_to_silver;
CREATE PROCEDURE move_to_silver (IN userId VARCHAR(255)) Update account_groups set group_name='Silver' where ssn=userId;
DROP PROCEDURE IF EXISTS move_to_gold;
CREATE PROCEDURE move_to_gold (IN userId VARCHAR(255)) Update account_groups set group_name='Gold' where ssn=userId;
DROP PROCEDURE IF EXISTS move_to_platinum;
CREATE PROCEDURE move_to_platinum (IN userId VARCHAR(255)) Update account_groups set group_name='Platinum' where ssn=userId;